import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TaskList from './components/TaskList';
import TaskForm from './components/TaskForm';

// Configure axios defaults
axios.defaults.baseURL = 'http://localhost:5000';
axios.defaults.withCredentials = true;
axios.defaults.headers.common['Content-Type'] = 'application/json';

const App = () => {
  const [lists, setLists] = useState([]);
  const [selectedList, setSelectedList] = useState(null);
  const [tasks, setTasks] = useState([]);

  // Fetch all task lists on load
  useEffect(() => {
    axios.get('http://localhost:5000/api/lists')
      .then(response => setLists(response.data))
      .catch(error => console.error('Error fetching task lists:', error));
  }, []);

  // Fetch tasks when a list is selected
  const fetchTasks = (listId) => {
    axios.get(`http://localhost:5000/api/lists/${listId}/tasks`)
      .then(response => {
        setTasks(response.data);
        setSelectedList(listId);
      })
      .catch(error => console.error('Error fetching tasks:', error));
  };

  return (
    <div className="App">
      <h1>Hierarchical Todo List App</h1>
      <div className="task-list-section">
        <h2>Task Lists</h2>
        {lists.map(list => (
          <div key={list.id} onClick={() => fetchTasks(list.id)}>
            {list.name}
          </div>
        ))}
      </div>

      {selectedList && (
        <div className="task-section">
          <TaskForm selectedList={selectedList} setTasks={setTasks} />
          <TaskList tasks={tasks} setTasks={setTasks} selectedList={selectedList} />
        </div>
      )}
    </div>
  );
};

export default App;
